import React, {useEffect, useRef} from "react";
import './Slider.css';
import img1 from '../../Images/img1.jpg';
import img2 from '../../Images/img2.jpg';
import img3 from '../../Images/img3.jpg';
const Slider=()=>{
    const listRef = useRef(null);
    const dotsRef = useRef(null);

    useEffect(() => {
        const items = listRef.current.querySelectorAll('.item');
        const dots = dotsRef.current.querySelectorAll('li');
    }, []);

    return(
        <div className='slider'>
            <div className='list' ref={listRef}>
                <div className='item'>
                    <img src={img1} alt=''/>
                </div>
                <div className='item'>
                    <img src={img2} alt=''/>
                </div>
                <div className='item'>
                    <img src={img3} alt=''/>
                </div>
            </div>
            <div className='buttons'>
                <button id='prev'><ion-icon name="arrow-back-outline"></ion-icon></button>
                <button id='next'><ion-icon name="arrow-forward-outline"></ion-icon></button>
            </div>
            <ul className='dots' ref={dotsRef}>
                <li className='active'></li>
                <li></li>
                <li></li>
            </ul>
        </div>
    );
}

export default Slider;
