import React, { useEffect, useRef, useState } from "react";
import './Slider.css';
import img1 from '../../Images/img1.jpg';
import img2 from '../../Images/img2.jpg';
import img3 from '../../Images/img3.jpg';

const Slider = () => {
    const listRef = useRef(null);
    const dotsRef = useRef(null);

    const [active, setActive] = useState(0);
    const [items, setItems] = useState([]);
    const [dots, setDots] = useState([]);

    useEffect(() => {
        const items = listRef.current.querySelectorAll('.item');
        const dots = dotsRef.current.querySelectorAll('li');
        setItems(items);
        setDots(dots);
    }, []);

    useEffect(() => {
        const refreshSlider = setInterval(() => {
            nextAction();
        }, 3000);

        return () => clearInterval(refreshSlider);
    }, [active, items]);

    const prevAction = () => {
        if (!items.length) return;
        let newActive = active - 1;
        if (newActive < 0) {
            newActive = items.length - 1;
        }
        setActive(newActive);
        reloadSlider(newActive);
    };

    const nextAction = () => {
        if (!items.length) return;
        let newActive = active + 1;
        if (newActive >= items.length) {
            newActive = 0;
        }
        setActive(newActive);
        reloadSlider(newActive);
    };

    const reloadSlider = (newActive) => {
        if (!items.length || !dots.length) return;

        const checkLeft = items[newActive].offsetLeft;
        listRef.current.style.transform = `translateX(-${checkLeft}px)`;

        const lastActiveDot = dotsRef.current.querySelector('.active');
        if (lastActiveDot) {
            lastActiveDot.classList.remove('active');
        }
        if (dots[newActive]) {
            dots[newActive].classList.add('active');
        }
    };

    const handleDotClick = (index) => {
        setActive(index);
        reloadSlider(index);
    };

    return (
        <div className='slider'>
            <div className='list' ref={listRef}>
                <div className='item'>
                    <img src={img1} alt='' />
                </div>
                <div className='item'>
                    <img src={img2} alt='' />
                </div>
                <div className='item'>
                    <img src={img3} alt='' />
                </div>
            </div>
            <div className='buttons'>
                <button id='prev' onClick={prevAction}>
                    <ion-icon name="arrow-back-outline"></ion-icon>
                </button>
                <button id='next' onClick={nextAction}>
                    <ion-icon name="arrow-forward-outline"></ion-icon>
                </button>
            </div>
            <ul className='dots' ref={dotsRef}>
                {[...Array(items.length)].map((_, index) => (
                    <li
                        key={index}
                        className={index === active ? 'active' : ''}
                        onClick={() => handleDotClick(index)}
                    ></li>
                ))}
            </ul>
        </div>
    );
}

export default Slider;
