import React, {useContext} from "react";
import './CartItems.css'
import {Link} from "react-router-dom";
import {ShopContext} from "../../Context/BarContext";
const CartItems=(props)=>{
    const {all_product, cartItems, addToCart, removeFromCart} = useContext(ShopContext);
    return(
        <div className='cartitems'>
            <div className='cartitems-format-main'>
                <p>Блюда</p>
                <p>Название</p>
                <p>Цена</p>
                <p>Количество</p>
                <p>Общее</p>
                <p>Убрать</p>
            </div>
            <hr/>
            <div className='cartitems-format'>
                <img src='' alt='' className='carticon-product-icon'/>
                <p></p>
                <p></p>
                <button className='cartitems-quantity'></button>
                <p></p>
                <span onClick={()=>{addToCart()}}><ion-icon name="add-outline"></ion-icon></span>
                <span onClick={()=>{removeFromCart()}}><ion-icon name="remove-outline"></ion-icon></span>
            </div>
        </div>
    );
}

export default CartItems;
