import React, {useEffect, useRef} from "react";
import './Slider.css';
import img1 from '../../Images/img1.jpg';
import img2 from '../../Images/img2.jpg';
import img3 from '../../Images/img3.jpg';
import item from "../Item/Item";
const Slider=()=>{
    const listRef = useRef(null);
    const dotsRef = useRef(null);
    const prevRef = useRef(null);
    const nextRef = useRef(null);

    let active = 0;
    let items;
    let dots;

    useEffect(() => {
        items = listRef.current.querySelectorAll('.item');
        dots = dotsRef.current.querySelectorAll('li');

        dots.forEach((li, key) => {
           li.addEventListener('click', () => {
              active = key;
              reloadSlider();
           });
        });
    }, []);

    const prevAction = () => {
        active -= 1;
        if (active < 0) {
            active = items.length - 1;
        }
        reloadSlider();
    };

    const nextAction = () => {
        active += 1;
        if (active >= items.length) {
            active = 0;
        }
        reloadSlider();
    }

    let refreshSlider = setInterval(() => {next})

    function reloadSlider(){
        const checkLeft = items[active].offsetLeft;
       // listRef.current.style.left = -checkLeft + 'px';
        listRef.current.style.transform = `translateX(-${checkLeft}px)`;

        let lastActiveDot = dotsRef.current.querySelector('.active');
        lastActiveDot.classList.remove('active');
        dots[active].classList.add('active');
    }

    return(
        <div className='slider'>
            <div className='list' ref={listRef}>
                <div className='item'>
                    <img src={img1} alt=''/>
                </div>
                <div className='item'>
                    <img src={img2} alt=''/>
                </div>
                <div className='item'>
                    <img src={img3} alt=''/>
                </div>
            </div>
            <div className='buttons'>
                <button id='prev' ref={prevRef} onClick={prevAction}><ion-icon name="arrow-back-outline"></ion-icon></button>
                <button id='next' ref={nextRef} onClick={nextAction}><ion-icon name="arrow-forward-outline"></ion-icon></button>
            </div>
            <ul className='dots' ref={dotsRef}>
                <li className='active'></li>
                <li></li>
                <li></li>
            </ul>
        </div>
    );
}

export default Slider;
